import React, { Component } from "react";
import { connect } from "react-redux";

class Counter extends Component {
    style = {
        fontSize: "12pt",
        padding: "5px 15px",
    }

    constructor(props) {
        super(props);
        this.doAction = this.doAction.bind(this);
        this.reset = this.reset.bind(this);
    }
}
